import React, { useState } from 'react';
import {
  Container,
  Typography,
  Box,
  TextField,
  Card,
  CardContent,
  Grid,
  AppBar,
  Toolbar,
  Autocomplete,
  Chip,
  Paper,
} from '@mui/material';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import LocalBarIcon from '@mui/icons-material/LocalBar';
import RestaurantIcon from '@mui/icons-material/Restaurant';
import NightlifeIcon from '@mui/icons-material/Nightlife';
import ShoppingBagIcon from '@mui/icons-material/ShoppingBag';

// Define types for our data
interface Hotspot {
  name: string;
  description: string;
  category: string;
  icon: React.ReactNode;
}

interface College {
  name: string;
  hotspots: Hotspot[];
}

// Sample data
const colleges: College[] = [
  {
    name: 'University of Wisconsin - Madison',
    hotspots: [
      {
        name: 'State Street Brats',
        description: 'Iconic sports bar and restaurant known for its bratwurst and game day atmosphere',
        category: 'Bar & Restaurant',
        icon: <LocalBarIcon />
      },
      {
        name: 'Memorial Union Terrace',
        description: 'Lakeside hangout with live music, food, and beautiful views of Lake Mendota',
        category: 'Entertainment',
        icon: <NightlifeIcon />
      },
      {
        name: 'Hilldale Shopping Center',
        description: 'Upscale shopping mall with restaurants, movie theater, and retail stores',
        category: 'Shopping',
        icon: <ShoppingBagIcon />
      },
      {
        name: 'Ian\'s Pizza',
        description: 'Famous late-night pizza spot known for unique toppings and quick service',
        category: 'Food',
        icon: <RestaurantIcon />
      }
    ]
  },
  {
    name: 'Vanderbilt University',
    hotspots: [
      {
        name: 'Broadway',
        description: 'Historic district with bars, restaurants, and live music venues',
        category: 'Entertainment',
        icon: <NightlifeIcon />
      },
      {
        name: 'Hattie B\'s Hot Chicken',
        description: 'Nashville\'s famous hot chicken restaurant, a must-try for visitors',
        category: 'Food',
        icon: <RestaurantIcon />
      },
      {
        name: 'The Gulch',
        description: 'Trendy neighborhood with boutique shops, restaurants, and nightlife',
        category: 'Shopping',
        icon: <ShoppingBagIcon />
      },
      {
        name: 'Pinewood Social',
        description: 'Unique venue with bowling, pool, and craft cocktails',
        category: 'Bar & Restaurant',
        icon: <LocalBarIcon />
      }
    ]
  },
  {
    name: 'Southern Methodist University',
    hotspots: [
      {
        name: 'Knox-Henderson',
        description: 'Trendy district with boutiques, restaurants, and bars',
        category: 'Shopping',
        icon: <ShoppingBagIcon />
      },
      {
        name: 'Uptown',
        description: 'Vibrant area with shopping, dining, and entertainment options',
        category: 'Entertainment',
        icon: <NightlifeIcon />
      },
      {
        name: 'The Rustic',
        description: 'Popular venue with live music, outdoor seating, and Texas cuisine',
        category: 'Bar & Restaurant',
        icon: <LocalBarIcon />
      },
      {
        name: 'Mi Cocina',
        description: 'Beloved Tex-Mex restaurant known for its margaritas and fajitas',
        category: 'Food',
        icon: <RestaurantIcon />
      }
    ]
  }
];

const theme = createTheme({
  palette: {
    primary: {
      main: '#FF4B2B',
    },
    secondary: {
      main: '#FF416C',
    },
    background: {
      default: '#f5f5f5',
    },
  },
  typography: {
    fontFamily: '"Poppins", "Roboto", "Helvetica", "Arial", sans-serif',
    h4: {
      fontWeight: 600,
    },
    h5: {
      fontWeight: 600,
    },
    h6: {
      fontWeight: 600,
    },
  },
  components: {
    MuiCard: {
      styleOverrides: {
        root: {
          borderRadius: 16,
          transition: 'transform 0.2s ease-in-out',
          '&:hover': {
            transform: 'translateY(-4px)',
          },
        },
      },
    },
    MuiAppBar: {
      styleOverrides: {
        root: {
          background: 'linear-gradient(45deg, #FF4B2B 30%, #FF416C 90%)',
        },
      },
    },
  },
});

function App() {
  const [selectedCollege, setSelectedCollege] = useState<College | null>(null);

  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ flexGrow: 1, minHeight: '100vh', bgcolor: 'background.default' }}>
        <AppBar position="static" elevation={0}>
          <Toolbar>
            <Typography variant="h5" component="div" sx={{ flexGrow: 1, fontWeight: 'bold' }}>
              OffCampus
            </Typography>
          </Toolbar>
        </AppBar>

        <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
          <Paper elevation={0} sx={{ p: 4, borderRadius: 4, bgcolor: 'white' }}>
            <Typography variant="h4" component="h1" gutterBottom sx={{ color: 'primary.main' }}>
              Discover Off-Campus Hotspots
            </Typography>

            <Typography variant="subtitle1" color="text.secondary" sx={{ mb: 4 }}>
              Find the best places to eat, drink, and hang out near your campus
            </Typography>

            <Autocomplete
              options={colleges}
              getOptionLabel={(option) => option.name}
              value={selectedCollege}
              onChange={(event, newValue) => {
                setSelectedCollege(newValue);
              }}
              renderInput={(params) => (
                <TextField
                  {...params}
                  label="Select a College"
                  variant="outlined"
                  fullWidth
                  sx={{ mb: 4 }}
                />
              )}
            />

            {selectedCollege && (
              <Box sx={{ mt: 4 }}>
                <Typography variant="h5" gutterBottom sx={{ color: 'primary.main' }}>
                  Popular Spots Near {selectedCollege.name}
                </Typography>
                <Grid container spacing={3}>
                  {selectedCollege.hotspots.map((hotspot, index) => (
                    <Grid item xs={12} sm={6} md={4} key={index}>
                      <Card elevation={2}>
                        <CardContent>
                          <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                            <Box sx={{ color: 'primary.main', mr: 1 }}>
                              {hotspot.icon}
                            </Box>
                            <Typography variant="h6" component="div">
                              {hotspot.name}
                            </Typography>
                          </Box>
                          <Chip
                            label={hotspot.category}
                            size="small"
                            sx={{
                              mb: 2,
                              bgcolor: 'primary.main',
                              color: 'white',
                              '&:hover': {
                                bgcolor: 'primary.dark',
                              },
                            }}
                          />
                          <Typography variant="body2" color="text.secondary">
                            {hotspot.description}
                          </Typography>
                        </CardContent>
                      </Card>
                    </Grid>
                  ))}
                </Grid>
              </Box>
            )}
          </Paper>
        </Container>
      </Box>
    </ThemeProvider>
  );
}

export default App;
