  palette: {
    primary: {
      main: '#C33764', // deep pink
    },
    secondary: {
      main: '#F8FFAE', // light yellow accent
    },
    background: {
      default: 'linear-gradient(180deg, #C33764 0%, #F8FFAE 100%)',
      paper: 'rgba(255,255,255,0.95)'
    },
  },
  typography: {
    fontFamily: 'Poppins, Arial, sans-serif',
    h1: {
      fontWeight: 900,
      fontSize: '4rem',
      letterSpacing: '-0.04em',
    },
    h4: {
      fontWeight: 700,
    },
    h5: {
      fontWeight: 600,
    },
    h6: {
      fontWeight: 600,
    },
    button: {
      fontWeight: 700,
      textTransform: 'none',
