import React, { useState } from 'react';
import {
  Container,
  Typography,
  Box,
  TextField,
  Card,
  CardContent,
  Grid,
  AppBar,
  Toolbar,
  Autocomplete,
} from '@mui/material';
import { ThemeProvider, createTheme } from '@mui/material/styles';

// Define types for our data
interface Hotspot {
  name: string;
  description: string;
  category: string;
}

interface College {
  name: string;
  hotspots: Hotspot[];
}

// Sample data
const colleges: College[] = [
  {
    name: 'University of Wisconsin - Madison',
    hotspots: [
      {
        name: 'Memorial Union Terrace',
        description: 'Iconic lakeside hangout spot with live music and beautiful views',
        category: 'Social'
      },
      {
        name: 'State Street',
        description: 'Popular shopping and dining district near campus',
        category: 'Entertainment'
      },
      {
        name: 'Camp Randall Stadium',
        description: 'Home of Badgers football and major campus events',
        category: 'Sports'
      }
    ]
  },
  {
    name: 'Vanderbilt University',
    hotspots: [
      {
        name: 'The Commons Center',
        description: 'Central hub for dining and student activities',
        category: 'Social'
      },
      {
        name: '21st Avenue',
        description: 'Popular area for restaurants and nightlife',
        category: 'Entertainment'
      },
      {
        name: 'Memorial Gymnasium',
        description: 'Historic basketball arena and event venue',
        category: 'Sports'
      }
    ]
  },
  {
    name: 'Southern Methodist University',
    hotspots: [
      {
        name: 'The Boulevard',
        description: 'Main campus gathering spot for events and socializing',
        category: 'Social'
      },
      {
        name: 'Meadows Museum',
        description: 'Art museum with significant Spanish art collection',
        category: 'Culture'
      },
      {
        name: 'Gerald J. Ford Stadium',
        description: 'Home of SMU Mustangs football',
        category: 'Sports'
      }
    ]
  }
];

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
});

function App() {
  const [selectedCollege, setSelectedCollege] = useState<College | null>(null);

  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ flexGrow: 1 }}>
        <AppBar position="static">
          <Toolbar>
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
              Campus Hotspots
            </Typography>
          </Toolbar>
        </AppBar>

        <Container maxWidth="lg" sx={{ mt: 4 }}>
          <Typography variant="h4" component="h1" gutterBottom>
            Discover Campus Hotspots
          </Typography>

          <Autocomplete
            options={colleges}
            getOptionLabel={(option) => option.name}
            value={selectedCollege}
            onChange={(event, newValue) => {
              setSelectedCollege(newValue);
            }}
            renderInput={(params) => (
              <TextField
                {...params}
                label="Select a College"
                variant="outlined"
                fullWidth
                sx={{ mb: 4 }}
              />
            )}
          />

          {selectedCollege && (
            <Box sx={{ mt: 4 }}>
              <Typography variant="h5" gutterBottom>
                Hotspots at {selectedCollege.name}
              </Typography>
              <Grid container spacing={3}>
                {selectedCollege.hotspots.map((hotspot, index) => (
                  <Grid item xs={12} sm={6} md={4} key={index}>
                    <Card>
                      <CardContent>
                        <Typography variant="h6" component="div">
                          {hotspot.name}
                        </Typography>
                        <Typography color="text.secondary" gutterBottom>
                          {hotspot.category}
                        </Typography>
                        <Typography variant="body2">
                          {hotspot.description}
                        </Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            </Box>
          )}
        </Container>
      </Box>
    </ThemeProvider>
  );
}

export default App;
