import React, { useState } from 'react';
import {
  Container,
  Typography,
  Box,
  TextField,
  Card,
  CardContent,
  Grid,
  AppBar,
  Toolbar,
  Autocomplete,
  Chip,
  Paper,
  Button,
  IconButton,
  Link,
} from '@mui/material';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import LocalBarIcon from '@mui/icons-material/LocalBar';
import RestaurantIcon from '@mui/icons-material/Restaurant';
import NightlifeIcon from '@mui/icons-material/Nightlife';
import ShoppingBagIcon from '@mui/icons-material/ShoppingBag';
import FavoriteIcon from '@mui/icons-material/Favorite';
import CardGiftcardIcon from '@mui/icons-material/CardGiftcard';
import SchoolIcon from '@mui/icons-material/School';
import HomeIcon from '@mui/icons-material/Home';
import SportsBarIcon from '@mui/icons-material/SportsBar';

// Define types for our data
interface GiftSpot {
  name: string;
  description: string;
  category: string;
  icon: React.ReactNode;
  priceRange: string;
  giftIdeas: string[];
  website: string;
}

interface College {
  name: string;
  hotspots: GiftSpot[];
}

// Sample data
const colleges: College[] = [
  {
    name: 'University of Wisconsin - Madison',
    hotspots: [
      {
        name: 'Kollege Klub',
        description: 'Iconic campus bar known for its classic college atmosphere and Badger memorabilia.',
        category: 'Badger Spirit',
        icon: <LocalBarIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Badger T-shirts'],
        website: 'https://www.kollegeklub.com'
      },
      {
        name: 'Whiskey Jacks Saloon',
        description: 'Popular country-themed bar with live music and dancing.',
        category: 'Entertainment',
        icon: <NightlifeIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Merchandise'],
        website: 'https://www.whiskeyjacks.com'
      },
      {
        name: 'City Bar',
        description: 'Upscale sports bar with extensive menu and game day atmosphere.',
        category: 'Food & Sports',
        icon: <RestaurantIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Sports Merchandise'],
        website: 'https://www.citybar.com'
      },
      {
        name: 'Ian\'s Pizza',
        description: 'Famous late-night pizza spot known for unique toppings.',
        category: 'Food & Dining',
        icon: <RestaurantIcon />,
        priceRange: '$',
        giftIdeas: ['Gift Cards', 'T-shirts'],
        website: 'https://www.ianspizza.com'
      },
      {
        name: 'Raising Cane\'s',
        description: 'Popular chicken finger restaurant with signature sauce.',
        category: 'Food & Dining',
        icon: <RestaurantIcon />,
        priceRange: '$',
        giftIdeas: ['Gift Cards', 'T-shirts'],
        website: 'https://www.raisingcanes.com'
      },
      {
        name: 'Monday\'s Bar',
        description: 'Classic college bar with great deals and lively atmosphere.',
        category: 'Entertainment',
        icon: <LocalBarIcon />,
        priceRange: '$',
        giftIdeas: ['Gift Cards', 'Merchandise'],
        website: 'https://www.mondaysbar.com'
      },
      {
        name: 'University Ridge Golf Course',
        description: 'Beautiful 18-hole golf course with stunning views.',
        category: 'Experiences',
        icon: <NightlifeIcon />,
        priceRange: '$$$',
        giftIdeas: ['Gift Cards', 'Golf Merchandise'],
        website: 'https://www.universityridge.com'
      },
      {
        name: 'The Bookstore',
        description: 'Official UW-Madison bookstore with extensive Badger merchandise.',
        category: 'Shopping',
        icon: <ShoppingBagIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Badger Apparel'],
        website: 'https://www.uwbookstore.com'
      },
      {
        name: 'Hawks Bar and Grill',
        description: 'Sports bar with great food and game day atmosphere.',
        category: 'Food & Sports',
        icon: <LocalBarIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Sports Merchandise'],
        website: 'https://www.hawksbar.com'
      },
      {
        name: 'Red Rock Saloon',
        description: 'Country-themed bar with mechanical bull and live music.',
        category: 'Entertainment',
        icon: <NightlifeIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Merchandise'],
        website: 'https://www.redrocksaloon.com'
      },
      {
        name: 'Sconnie Bar',
        description: 'Popular campus bar known for its Wisconsin-themed drinks.',
        category: 'Badger Spirit',
        icon: <LocalBarIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Badger Merchandise'],
        website: 'https://www.sconniebar.com'
      }
    ]
  },
  {
    name: 'Vanderbilt University',
    hotspots: [
      {
        name: 'Vol Elrods',
        description: 'Classic Nashville bar with a rich history and great atmosphere.',
        category: 'Vandy Spirit',
        icon: <LocalBarIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Vandy Merchandise'],
        website: 'https://www.volelrods.com'
      },
      {
        name: 'Jason Aldean\'s Kitchen + Rooftop Bar',
        description: 'Country star\'s upscale restaurant with rooftop views and live music.',
        category: 'Entertainment',
        icon: <NightlifeIcon />,
        priceRange: '$$$',
        giftIdeas: ['Gift Cards', 'Merchandise'],
        website: 'https://www.jasonaldeans.com'
      },
      {
        name: 'Friends Bar',
        description: 'Popular sports bar with great game day atmosphere and food specials.',
        category: 'Sports & Dining',
        icon: <LocalBarIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Sports Merchandise'],
        website: 'https://www.friendsbar.com'
      },
      {
        name: 'Kid Rock\'s Big Honky Tonk',
        description: 'Massive entertainment venue with live music and multiple bars.',
        category: 'Entertainment',
        icon: <NightlifeIcon />,
        priceRange: '$$$',
        giftIdeas: ['Gift Cards', 'Merchandise'],
        website: 'https://www.kidrocksbigasshonkytonk.com'
      },
      {
        name: 'Losers Bar and Grill',
        description: 'Beloved sports bar with great food and game day specials.',
        category: 'Sports & Dining',
        icon: <LocalBarIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Sports Merchandise'],
        website: 'https://www.losersbar.com'
      },
      {
        name: 'Taco Mama',
        description: 'Fresh and creative Mexican restaurant with a fun atmosphere.',
        category: 'Food & Dining',
        icon: <RestaurantIcon />,
        priceRange: '$',
        giftIdeas: ['Gift Cards', 'T-shirts'],
        website: 'https://www.tacomama.com'
      },
      {
        name: 'Raising Cane\'s',
        description: 'Popular chicken finger restaurant with signature sauce.',
        category: 'Food & Dining',
        icon: <RestaurantIcon />,
        priceRange: '$',
        giftIdeas: ['Gift Cards', 'T-shirts'],
        website: 'https://www.raisingcanes.com'
      },
      {
        name: 'San Antonio Taco Co',
        description: 'Nashville\'s original taco spot with a unique atmosphere.',
        category: 'Food & Dining',
        icon: <RestaurantIcon />,
        priceRange: '$',
        giftIdeas: ['Gift Cards', 'T-shirts'],
        website: 'https://www.satco.com'
      },
      {
        name: 'Velvet Taco',
        description: 'Creative taco restaurant with unique flavors and late-night hours.',
        category: 'Food & Dining',
        icon: <RestaurantIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'T-shirts'],
        website: 'https://www.velvettaco.com'
      },
      {
        name: 'Martin\'s Bar-B-Que Joint',
        description: 'Authentic Tennessee barbecue with a great atmosphere.',
        category: 'Food & Dining',
        icon: <RestaurantIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'BBQ Merchandise'],
        website: 'https://www.martinsbbqjoint.com'
      },
      {
        name: 'Nashville Sounds Baseball',
        description: 'Minor league baseball team with a beautiful stadium.',
        category: 'Sports & Entertainment',
        icon: <NightlifeIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Team Merchandise'],
        website: 'https://www.milb.com/nashville'
      },
      {
        name: 'Barcelona Wine Bar',
        description: 'Upscale Spanish tapas restaurant with extensive wine selection.',
        category: 'Fine Dining',
        icon: <RestaurantIcon />,
        priceRange: '$$$',
        giftIdeas: ['Gift Cards', 'Wine Merchandise'],
        website: 'https://www.barcelonawinebar.com'
      }
    ]
  },
  {
    name: 'Southern Methodist University',
    hotspots: [
      {
        name: 'Knox-Henderson',
        description: 'Trendy district with boutiques perfect for student fashion and gifts',
        category: 'Shopping',
        icon: <ShoppingBagIcon />,
        priceRange: '$$$',
        giftIdeas: ['Gift Cards', 'Fashion Merchandise'],
        website: 'https://www.knoxhenderson.com'
      },
      {
        name: 'The Rustic',
        description: 'Popular venue with Texas charm - great for experience gifts',
        category: 'Experiences',
        icon: <LocalBarIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Texas Merchandise'],
        website: 'https://www.therustic.com'
      },
      {
        name: 'Mi Cocina',
        description: 'Beloved Tex-Mex restaurant - perfect for foodie gifts',
        category: 'Food & Dining',
        icon: <RestaurantIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'T-shirts'],
        website: 'https://www.micocina.com'
      }
    ]
  }
];

const theme = createTheme({
  palette: {
    primary: {
      main: '#2E3B55',
    },
    secondary: {
      main: '#E63946',
    },
    background: {
      default: '#F1FAEE',
    },
  },
  typography: {
    fontFamily: '"Poppins", "Roboto", "Helvetica", "Arial", sans-serif',
    h4: {
      fontWeight: 600,
    },
    h5: {
      fontWeight: 600,
    },
    h6: {
      fontWeight: 600,
    },
  },
  components: {
    MuiCard: {
      styleOverrides: {
        root: {
          borderRadius: 16,
          transition: 'transform 0.2s ease-in-out',
          '&:hover': {
            transform: 'translateY(-4px)',
          },
        },
      },
    },
    MuiAppBar: {
      styleOverrides: {
        root: {
          background: 'linear-gradient(45deg, #2E3B55 30%, #1D3557 90%)',
        },
      },
    },
  },
});

// Custom Logo Component
const CustomLogo = ({ size = 'large' }) => {
  const isLarge = size === 'large';
  const boxSize = isLarge ? 200 : 120;
  const fontSize = isLarge ? '2.5rem' : '1.5rem';

  return (
    <Box
      sx={{
        position: 'relative',
        width: boxSize,
        height: boxSize,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
      }}
    >
      {/* Gift Box Container */}
      <Box
        sx={{
          position: 'relative',
          width: '100%',
          height: '100%',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          '&:hover': {
            '& .ribbon': {
              transform: 'translateY(-5px)',
            },
            '& .box': {
              transform: 'scale(1.02)',
            },
          },
        }}
      >
        {/* Gift Box */}
        <Box
          className="box"
          sx={{
            position: 'relative',
            width: '80%',
            height: '80%',
            backgroundColor: 'white',
            borderRadius: '12px',
            boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
            transition: 'transform 0.3s ease',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            overflow: 'hidden',
            '&::before': {
              content: '""',
              position: 'absolute',
              top: 0,
              left: 0,
              right: 0,
              height: '4px',
              background: 'linear-gradient(90deg, #2E3B55, #E63946)',
            },
          }}
        >
          {/* Ribbon */}
          <Box
            className="ribbon"
            sx={{
              position: 'absolute',
              top: '-20%',
              left: '50%',
              transform: 'translateX(-50%)',
              width: '4px',
              height: '40%',
              backgroundColor: '#E63946',
              transition: 'transform 0.3s ease',
              '&::before': {
                content: '""',
                position: 'absolute',
                top: '100%',
                left: '50%',
                transform: 'translateX(-50%)',
                borderLeft: '8px solid transparent',
                borderRight: '8px solid transparent',
                borderTop: '12px solid #E63946',
              },
            }}
          />

          {/* Text */}
          <Typography
            variant="h1"
            sx={{
              fontSize,
              fontWeight: 700,
              background: 'linear-gradient(45deg, #2E3B55, #E63946)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              textAlign: 'center',
              letterSpacing: '-0.02em',
              fontFamily: '"Poppins", sans-serif',
            }}
          >
            OffCampus
          </Typography>
        </Box>
      </Box>
    </Box>
  );
};

function App() {
  const [selectedCollege, setSelectedCollege] = useState<College | null>(null);
  const [showHome, setShowHome] = useState(true);

  const HomePage = () => (
    <Box sx={{ textAlign: 'center', py: 8 }}>
      <Box sx={{ display: 'flex', justifyContent: 'center', mb: 4 }}>
        <CustomLogo size="large" />
      </Box>
      <Typography variant="h5" color="text.secondary" sx={{ mb: 4 }}>
        The Perfect Gift Guide for College Students
      </Typography>
      <Typography variant="body1" sx={{ maxWidth: 600, mx: 'auto', mb: 6 }}>
        Find the perfect gifts and experiences for students at top universities. 
        From campus essentials to memorable experiences, we've got you covered.
      </Typography>
      <Button
        variant="contained"
        size="large"
        onClick={() => setShowHome(false)}
        sx={{
          bgcolor: 'secondary.main',
          '&:hover': {
            bgcolor: 'secondary.dark',
          },
          px: 4,
          py: 1.5,
        }}
      >
        Explore Gift Ideas
      </Button>
    </Box>
  );

  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ flexGrow: 1, minHeight: '100vh', bgcolor: 'background.default' }}>
        <AppBar position="static" elevation={0}>
          <Toolbar>
            <Box sx={{ display: 'flex', alignItems: 'center', flexGrow: 1 }}>
              <CustomLogo size="small" />
            </Box>
            <IconButton 
              color="inherit" 
              onClick={() => setShowHome(true)}
              sx={{ ml: 2 }}
            >
              <HomeIcon />
            </IconButton>
          </Toolbar>
        </AppBar>

        <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
          {showHome ? (
            <HomePage />
          ) : (
            <Paper elevation={0} sx={{ p: 4, borderRadius: 4, bgcolor: 'white' }}>
              <Typography variant="h4" component="h1" gutterBottom sx={{ color: 'primary.main' }}>
                Find the Perfect Gift
              </Typography>

              <Typography variant="subtitle1" color="text.secondary" sx={{ mb: 4 }}>
                Select a college to discover gift ideas and popular spots near campus
              </Typography>

              <Autocomplete
                options={colleges}
                getOptionLabel={(option) => option.name}
                value={selectedCollege}
                onChange={(event, newValue) => {
                  setSelectedCollege(newValue);
                }}
                renderInput={(params) => (
                  <TextField
                    {...params}
                    label="Select a College"
                    variant="outlined"
                    fullWidth
                    sx={{ mb: 4 }}
                  />
                )}
              />

              {selectedCollege && (
                <Box sx={{ mt: 4 }}>
                  <Typography variant="h5" gutterBottom sx={{ color: 'primary.main' }}>
                    Gift Ideas Near {selectedCollege.name}
                  </Typography>
                  <Grid container spacing={3}>
                    {selectedCollege.hotspots.map((spot, index) => (
                      <Grid item xs={12} sm={6} md={4} key={index}>
                        <Card elevation={2} sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                          <CardContent sx={{ flexGrow: 1 }}>
                            <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                              <Box sx={{ color: 'primary.main', mr: 1 }}>
                                {spot.icon}
                              </Box>
                              <Typography variant="h6" component="div">
                                <Link href={spot.website} target="_blank" rel="noopener noreferrer" color="inherit" underline="hover">
                                  {spot.name}
                                </Link>
                              </Typography>
                            </Box>
                            <Box sx={{ mb: 2 }}>
                              <Chip
                                label={spot.category}
                                size="small"
                                sx={{
                                  mr: 1,
                                  bgcolor: 'primary.main',
                                  color: 'white',
                                }}
                              />
                              <Chip
                                label={spot.priceRange}
                                size="small"
                                sx={{
                                  bgcolor: 'secondary.main',
                                  color: 'white',
                                }}
                              />
                            </Box>
                            <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                              {spot.description}
                            </Typography>
                            <Typography variant="subtitle2" sx={{ color: 'primary.main', mb: 1 }}>
                              Gift Ideas:
                            </Typography>
                            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
                              {spot.giftIdeas.map((gift, idx) => (
                                <Chip
                                  key={idx}
                                  label={gift}
                                  size="small"
                                  variant="outlined"
                                  icon={<FavoriteIcon sx={{ fontSize: 16 }} />}
                                />
                              ))}
                            </Box>
                          </CardContent>
                        </Card>
                      </Grid>
                    ))}
                  </Grid>
                </Box>
              )}
            </Paper>
          )}
        </Container>
      </Box>
    </ThemeProvider>
  );
}

export default App;
