import React, { useState } from 'react';
import {
  Container,
  Typography,
  Box,
  TextField,
  Card,
  CardContent,
  Grid,
  AppBar,
  Toolbar,
  Autocomplete,
  Chip,
  Paper,
  Button,
  IconButton,
} from '@mui/material';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import LocalBarIcon from '@mui/icons-material/LocalBar';
import RestaurantIcon from '@mui/icons-material/Restaurant';
import NightlifeIcon from '@mui/icons-material/Nightlife';
import ShoppingBagIcon from '@mui/icons-material/ShoppingBag';
import FavoriteIcon from '@mui/icons-material/Favorite';
import CardGiftcardIcon from '@mui/icons-material/CardGiftcard';
import SchoolIcon from '@mui/icons-material/School';
import HomeIcon from '@mui/icons-material/Home';

// Define types for our data
interface GiftSpot {
  name: string;
  description: string;
  category: string;
  icon: React.ReactNode;
  priceRange: string;
  giftIdeas: string[];
}

interface College {
  name: string;
  hotspots: GiftSpot[];
}

// Sample data
const colleges: College[] = [
  {
    name: 'University of Wisconsin - Madison',
    hotspots: [
      {
        name: 'State Street Brats',
        description: 'Iconic sports bar and restaurant - perfect for game day gifts and Badger merchandise',
        category: 'Badger Spirit',
        icon: <LocalBarIcon />,
        priceRange: '$$',
        giftIdeas: ['Badger T-shirts', 'Game Day Tickets', 'Team Merchandise']
      },
      {
        name: 'Hilldale Shopping Center',
        description: 'Upscale shopping destination with stores perfect for student essentials and gifts',
        category: 'Shopping',
        icon: <ShoppingBagIcon />,
        priceRange: '$$$',
        giftIdeas: ['Laptop Accessories', 'Dorm Decor', 'Tech Gadgets']
      },
      {
        name: 'Ian\'s Pizza',
        description: 'Famous pizza spot - great for gift cards and group orders',
        category: 'Food & Dining',
        icon: <RestaurantIcon />,
        priceRange: '$',
        giftIdeas: ['Gift Cards', 'Group Meal Vouchers', 'Pizza Party Package']
      }
    ]
  },
  {
    name: 'Vanderbilt University',
    hotspots: [
      {
        name: 'The Gulch',
        description: 'Trendy shopping district with unique gifts and student essentials',
        category: 'Shopping',
        icon: <ShoppingBagIcon />,
        priceRange: '$$$',
        giftIdeas: ['Designer Backpacks', 'Study Supplies', 'Fashion Items']
      },
      {
        name: 'Pinewood Social',
        description: 'Unique venue perfect for special occasion gifts and experiences',
        category: 'Experiences',
        icon: <NightlifeIcon />,
        priceRange: '$$$',
        giftIdeas: ['Bowling Party Package', 'Cocktail Making Class', 'Group Event Vouchers']
      },
      {
        name: 'Hattie B\'s Hot Chicken',
        description: 'Nashville\'s famous hot chicken - great for foodie gifts',
        category: 'Food & Dining',
        icon: <RestaurantIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Hot Sauce Collection', 'Nashville Food Tour']
      }
    ]
  },
  {
    name: 'Southern Methodist University',
    hotspots: [
      {
        name: 'Knox-Henderson',
        description: 'Trendy district with boutiques perfect for student fashion and gifts',
        category: 'Shopping',
        icon: <ShoppingBagIcon />,
        priceRange: '$$$',
        giftIdeas: ['Designer Clothing', 'Accessories', 'Home Decor']
      },
      {
        name: 'The Rustic',
        description: 'Popular venue with Texas charm - great for experience gifts',
        category: 'Experiences',
        icon: <LocalBarIcon />,
        priceRange: '$$',
        giftIdeas: ['Concert Tickets', 'Dining Gift Cards', 'Texas-themed Gifts']
      },
      {
        name: 'Mi Cocina',
        description: 'Beloved Tex-Mex restaurant - perfect for foodie gifts',
        category: 'Food & Dining',
        icon: <RestaurantIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Cookbook', 'Tequila Tasting Experience']
      }
    ]
  }
];

const theme = createTheme({
  palette: {
    primary: {
      main: '#2E3B55',
    },
    secondary: {
      main: '#E63946',
    },
    background: {
      default: '#F1FAEE',
    },
  },
  typography: {
    fontFamily: '"Poppins", "Roboto", "Helvetica", "Arial", sans-serif',
    h4: {
      fontWeight: 600,
    },
    h5: {
      fontWeight: 600,
    },
    h6: {
      fontWeight: 600,
    },
  },
  components: {
    MuiCard: {
      styleOverrides: {
        root: {
          borderRadius: 16,
          transition: 'transform 0.2s ease-in-out',
          '&:hover': {
            transform: 'translateY(-4px)',
          },
        },
      },
    },
    MuiAppBar: {
      styleOverrides: {
        root: {
          background: 'linear-gradient(45deg, #2E3B55 30%, #1D3557 90%)',
        },
      },
    },
  },
});

function App() {
  const [selectedCollege, setSelectedCollege] = useState<College | null>(null);
  const [showHome, setShowHome] = useState(true);

  const HomePage = () => (
    <Box sx={{ textAlign: 'center', py: 8 }}>
      <Box sx={{ display: 'flex', justifyContent: 'center', mb: 4 }}>
        <CardGiftcardIcon sx={{ fontSize: 60, color: 'primary.main' }} />
        <SchoolIcon sx={{ fontSize: 60, color: 'secondary.main', ml: 2 }} />
      </Box>
      <Typography variant="h3" component="h1" gutterBottom sx={{ color: 'primary.main', fontWeight: 'bold' }}>
        OffCampus
      </Typography>
      <Typography variant="h5" color="text.secondary" sx={{ mb: 4 }}>
        The Perfect Gift Guide for College Students
      </Typography>
      <Typography variant="body1" sx={{ maxWidth: 600, mx: 'auto', mb: 6 }}>
        Find the perfect gifts and experiences for students at top universities. 
        From campus essentials to memorable experiences, we've got you covered.
      </Typography>
      <Button
        variant="contained"
        size="large"
        onClick={() => setShowHome(false)}
        sx={{
          bgcolor: 'secondary.main',
          '&:hover': {
            bgcolor: 'secondary.dark',
          },
          px: 4,
          py: 1.5,
        }}
      >
        Explore Gift Ideas
      </Button>
    </Box>
  );

  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ flexGrow: 1, minHeight: '100vh', bgcolor: 'background.default' }}>
        <AppBar position="static" elevation={0}>
          <Toolbar>
            <Box sx={{ display: 'flex', alignItems: 'center', flexGrow: 1 }}>
              <CardGiftcardIcon sx={{ mr: 1 }} />
              <Typography variant="h5" component="div" sx={{ fontWeight: 'bold' }}>
                OffCampus
              </Typography>
            </Box>
            <IconButton 
              color="inherit" 
              onClick={() => setShowHome(true)}
              sx={{ ml: 2 }}
            >
              <HomeIcon />
            </IconButton>
          </Toolbar>
        </AppBar>

        <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
          {showHome ? (
            <HomePage />
          ) : (
            <Paper elevation={0} sx={{ p: 4, borderRadius: 4, bgcolor: 'white' }}>
              <Typography variant="h4" component="h1" gutterBottom sx={{ color: 'primary.main' }}>
                Find the Perfect Gift
              </Typography>

              <Typography variant="subtitle1" color="text.secondary" sx={{ mb: 4 }}>
                Select a college to discover gift ideas and popular spots near campus
              </Typography>

              <Autocomplete
                options={colleges}
                getOptionLabel={(option) => option.name}
                value={selectedCollege}
                onChange={(event, newValue) => {
                  setSelectedCollege(newValue);
                }}
                renderInput={(params) => (
                  <TextField
                    {...params}
                    label="Select a College"
                    variant="outlined"
                    fullWidth
                    sx={{ mb: 4 }}
                  />
                )}
              />

              {selectedCollege && (
                <Box sx={{ mt: 4 }}>
                  <Typography variant="h5" gutterBottom sx={{ color: 'primary.main' }}>
                    Gift Ideas Near {selectedCollege.name}
                  </Typography>
                  <Grid container spacing={3}>
                    {selectedCollege.hotspots.map((spot, index) => (
                      <Grid item xs={12} sm={6} md={4} key={index}>
                        <Card elevation={2}>
                          <CardContent>
                            <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                              <Box sx={{ color: 'primary.main', mr: 1 }}>
                                {spot.icon}
                              </Box>
                              <Typography variant="h6" component="div">
                                {spot.name}
                              </Typography>
                            </Box>
                            <Box sx={{ mb: 2 }}>
                              <Chip
                                label={spot.category}
                                size="small"
                                sx={{
                                  mr: 1,
                                  bgcolor: 'primary.main',
                                  color: 'white',
                                }}
                              />
                              <Chip
                                label={spot.priceRange}
                                size="small"
                                sx={{
                                  bgcolor: 'secondary.main',
                                  color: 'white',
                                }}
                              />
                            </Box>
                            <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                              {spot.description}
                            </Typography>
                            <Typography variant="subtitle2" sx={{ color: 'primary.main', mb: 1 }}>
                              Popular Gift Ideas:
                            </Typography>
                            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
                              {spot.giftIdeas.map((gift, idx) => (
                                <Chip
                                  key={idx}
                                  label={gift}
                                  size="small"
                                  variant="outlined"
                                  icon={<FavoriteIcon sx={{ fontSize: 16 }} />}
                                />
                              ))}
                            </Box>
                          </CardContent>
                        </Card>
                      </Grid>
                    ))}
                  </Grid>
                </Box>
              )}
            </Paper>
          )}
        </Container>
      </Box>
    </ThemeProvider>
  );
}

export default App;
