import React, { useState } from 'react';
import {
  Container,
  Typography,
  Box,
  TextField,
  Card,
  CardContent,
  Grid,
  AppBar,
  Toolbar,
  Autocomplete,
  Chip,
  Paper,
  Button,
  IconButton,
  Link,
} from '@mui/material';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import LocalBarIcon from '@mui/icons-material/LocalBar';
import RestaurantIcon from '@mui/icons-material/Restaurant';
import NightlifeIcon from '@mui/icons-material/Nightlife';
import ShoppingBagIcon from '@mui/icons-material/ShoppingBag';
import FavoriteIcon from '@mui/icons-material/Favorite';
import CardGiftcardIcon from '@mui/icons-material/CardGiftcard';
import SchoolIcon from '@mui/icons-material/School';
import HomeIcon from '@mui/icons-material/Home';
import SportsBarIcon from '@mui/icons-material/SportsBar';

// Define types for our data
interface GiftSpot {
  name: string;
  description: string;
  category: string;
  icon: React.ReactNode;
  priceRange: string;
  giftIdeas: string[];
  website: string;
}

interface College {
  name: string;
  hotspots: GiftSpot[];
}

// Sample data
const colleges: College[] = [
  {
    name: 'University of Wisconsin - Madison',
    hotspots: [
      {
        name: 'Kollege Klub',
        description: 'Iconic campus bar known for its classic college atmosphere and Badger memorabilia.',
        category: 'Badger Spirit',
        icon: <LocalBarIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Badger T-shirts'],
        website: 'https://www.kollegeklub.com'
      },
      {
        name: 'Whiskey Jacks Saloon',
        description: 'Popular country-themed bar with live music and dancing.',
        category: 'Entertainment',
        icon: <NightlifeIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Merchandise'],
        website: 'https://www.whiskeyjacks.com'
      },
      {
        name: 'City Bar',
        description: 'Upscale sports bar with extensive menu and game day atmosphere.',
        category: 'Food & Sports',
        icon: <RestaurantIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Sports Merchandise'],
        website: 'https://www.citybar.com'
      },
      {
        name: 'Ian\'s Pizza',
        description: 'Famous late-night pizza spot known for unique toppings.',
        category: 'Food & Dining',
        icon: <RestaurantIcon />,
        priceRange: '$',
        giftIdeas: ['Gift Cards', 'T-shirts'],
        website: 'https://www.ianspizza.com'
      },
      {
        name: 'Raising Cane\'s',
        description: 'Popular chicken finger restaurant with signature sauce.',
        category: 'Food & Dining',
        icon: <RestaurantIcon />,
        priceRange: '$',
        giftIdeas: ['Gift Cards', 'T-shirts'],
        website: 'https://www.raisingcanes.com'
      },
      {
        name: 'Monday\'s Bar',
        description: 'Classic college bar with great deals and lively atmosphere.',
        category: 'Entertainment',
        icon: <LocalBarIcon />,
        priceRange: '$',
        giftIdeas: ['Gift Cards', 'Merchandise'],
        website: 'https://www.mondaysbar.com'
      },
      {
        name: 'University Ridge Golf Course',
        description: 'Beautiful 18-hole golf course with stunning views.',
        category: 'Experiences',
        icon: <NightlifeIcon />,
        priceRange: '$$$',
        giftIdeas: ['Gift Cards', 'Golf Merchandise'],
        website: 'https://www.universityridge.com'
      },
      {
        name: 'The Bookstore',
        description: 'Official UW-Madison bookstore with extensive Badger merchandise.',
        category: 'Shopping',
        icon: <ShoppingBagIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Badger Apparel'],
        website: 'https://www.uwbookstore.com'
      },
      {
        name: 'Hawks Bar and Grill',
        description: 'Sports bar with great food and game day atmosphere.',
        category: 'Food & Sports',
        icon: <LocalBarIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Sports Merchandise'],
        website: 'https://www.hawksbar.com'
      },
      {
        name: 'Red Rock Saloon',
        description: 'Country-themed bar with mechanical bull and live music.',
        category: 'Entertainment',
        icon: <NightlifeIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Merchandise'],
        website: 'https://www.redrocksaloon.com'
      },
      {
        name: 'Sconnie Bar',
        description: 'Popular campus bar known for its Wisconsin-themed drinks.',
        category: 'Badger Spirit',
        icon: <LocalBarIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Badger Merchandise'],
        website: 'https://www.sconniebar.com'
      }
    ]
  },
  {
    name: 'Vanderbilt University',
    hotspots: [
      {
        name: 'Vol Elrods',
        description: 'Classic Nashville bar with a rich history and great atmosphere.',
        category: 'Vandy Spirit',
        icon: <LocalBarIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Vandy Merchandise'],
        website: 'https://www.volelrods.com'
      },
      {
        name: 'Jason Aldean\'s Kitchen + Rooftop Bar',
        description: 'Country star\'s upscale restaurant with rooftop views and live music.',
        category: 'Entertainment',
        icon: <NightlifeIcon />,
        priceRange: '$$$',
        giftIdeas: ['Gift Cards', 'Merchandise'],
        website: 'https://www.jasonaldeans.com'
      },
      {
        name: 'Friends Bar',
        description: 'Popular sports bar with great game day atmosphere and food specials.',
        category: 'Sports & Dining',
        icon: <LocalBarIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Sports Merchandise'],
        website: 'https://www.friendsbar.com'
      },
      {
        name: 'Kid Rock\'s Big Honky Tonk',
        description: 'Massive entertainment venue with live music and multiple bars.',
        category: 'Entertainment',
        icon: <NightlifeIcon />,
        priceRange: '$$$',
        giftIdeas: ['Gift Cards', 'Merchandise'],
        website: 'https://www.kidrocksbigasshonkytonk.com'
      },
      {
        name: 'Losers Bar and Grill',
        description: 'Beloved sports bar with great food and game day specials.',
        category: 'Sports & Dining',
        icon: <LocalBarIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Sports Merchandise'],
        website: 'https://www.losersbar.com'
      },
      {
        name: 'Taco Mama',
        description: 'Fresh and creative Mexican restaurant with a fun atmosphere.',
        category: 'Food & Dining',
        icon: <RestaurantIcon />,
        priceRange: '$',
        giftIdeas: ['Gift Cards', 'T-shirts'],
        website: 'https://www.tacomama.com'
      },
      {
        name: 'Raising Cane\'s',
        description: 'Popular chicken finger restaurant with signature sauce.',
        category: 'Food & Dining',
        icon: <RestaurantIcon />,
        priceRange: '$',
        giftIdeas: ['Gift Cards', 'T-shirts'],
        website: 'https://www.raisingcanes.com'
      },
      {
        name: 'San Antonio Taco Co',
        description: 'Nashville\'s original taco spot with a unique atmosphere.',
        category: 'Food & Dining',
        icon: <RestaurantIcon />,
        priceRange: '$',
        giftIdeas: ['Gift Cards', 'T-shirts'],
        website: 'https://www.satco.com'
      },
      {
        name: 'Velvet Taco',
        description: 'Creative taco restaurant with unique flavors and late-night hours.',
        category: 'Food & Dining',
        icon: <RestaurantIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'T-shirts'],
        website: 'https://www.velvettaco.com'
      },
      {
        name: 'Martin\'s Bar-B-Que Joint',
        description: 'Authentic Tennessee barbecue with a great atmosphere.',
        category: 'Food & Dining',
        icon: <RestaurantIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'BBQ Merchandise'],
        website: 'https://www.martinsbbqjoint.com'
      },
      {
        name: 'Nashville Sounds Baseball',
        description: 'Minor league baseball team with a beautiful stadium.',
        category: 'Sports & Entertainment',
        icon: <NightlifeIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Cards', 'Team Merchandise'],
        website: 'https://www.milb.com/nashville'
      },
      {
        name: 'Barcelona Wine Bar',
        description: 'Upscale Spanish tapas restaurant with extensive wine selection.',
        category: 'Fine Dining',
        icon: <RestaurantIcon />,
        priceRange: '$$$',
        giftIdeas: ['Gift Cards', 'Wine Merchandise'],
        website: 'https://www.barcelonawinebar.com'
      }
    ]
  },
  {
    name: 'Southern Methodist University',
    hotspots: [
      {
        name: 'Goodbye Horses',
        description: 'Trendy bar and lounge popular with SMU students. Great for a night out or casual gatherings.',
        category: 'Bar & Lounge',
        icon: <LocalBarIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Card', 'T-shirt'],
        website: 'https://www.goodbyehorsesdallas.com'
      },
      {
        name: 'Old Monk',
        description: 'Classic pub with a cozy atmosphere and a great beer selection. Perfect for casual meetups.',
        category: 'Pub',
        icon: <LocalBarIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Card', 'T-shirt'],
        website: 'https://www.oldmonkdallas.com'
      },
      {
        name: 'Theory',
        description: 'Lively sports bar and club, a favorite for game days and late-night fun.',
        category: 'Sports Bar',
        icon: <NightlifeIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Card', 'T-shirt'],
        website: 'https://www.theorydallas.com'
      },
      {
        name: 'Komodo',
        description: 'Trendy Asian fusion restaurant and lounge, perfect for group dinners and celebrations.',
        category: 'Restaurant & Lounge',
        icon: <RestaurantIcon />,
        priceRange: '$$$',
        giftIdeas: ['Gift Card', 'T-shirt'],
        website: 'https://www.komododallas.com'
      },
      {
        name: 'Shugs',
        description: 'Popular spot for drinks and socializing, known for its fun vibe and creative cocktails.',
        category: 'Bar',
        icon: <LocalBarIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Card', 'T-shirt'],
        website: 'https://www.shugsdallas.com'
      },
      {
        name: 'Phi Delt House',
        description: 'Fraternity house known for its vibrant social events and strong campus presence. Great for SMU spirit gifts.',
        category: 'Fraternity',
        icon: <HomeIcon />,
        priceRange: '$$',
        giftIdeas: ['Gift Card', 'T-shirt'],
        website: 'https://www.smu.edu/studentaffairs/greeklife'
      }
    ]
  }
];

const theme = createTheme({
  palette: {
    primary: {
      main: '#2E3B55',
    },
    secondary: {
      main: '#E63946',
    },
    background: {
      default: '#F1FAEE',
    },
  },
  typography: {
    fontFamily: '"Poppins", "Roboto", "Helvetica", "Arial", sans-serif',
    h4: {
      fontWeight: 600,
    },
    h5: {
      fontWeight: 600,
    },
    h6: {
      fontWeight: 600,
    },
  },
  components: {
    MuiCard: {
      styleOverrides: {
        root: {
          borderRadius: 16,
          transition: 'transform 0.2s ease-in-out',
          '&:hover': {
            transform: 'translateY(-4px)',
          },
        },
      },
    },
    MuiAppBar: {
      styleOverrides: {
        root: {
          background: 'linear-gradient(45deg, #2E3B55 30%, #1D3557 90%)',
        },
      },
    },
  },
});

// Minimalist OC logo for OffCampus
const OffCampusLogo = ({ size = 'large' }) => {
  const isLarge = size === 'large';
  const boxSize = isLarge ? 80 : 44;
  const fontSize = isLarge ? '2.7rem' : '1.3rem';
  return (
    <Box
      sx={{
        width: boxSize,
        height: boxSize,
        borderRadius: 3,
        bgcolor: 'primary.main',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        boxShadow: 3,
        userSelect: 'none',
      }}
    >
      <Typography
        variant="h1"
        sx={{
          fontSize,
          fontWeight: 800,
          color: 'white',
          fontFamily: 'Poppins, Arial, sans-serif',
          letterSpacing: '-0.04em',
          lineHeight: 1,
        }}
      >
        OC
      </Typography>
    </Box>
  );
};

function getGiftIcon(gift: string) {
  if (/card/i.test(gift)) return '🎁';
  if (/shirt|apparel|t-shirt/i.test(gift)) return '👕';
  if (/merch|souvenir|shopping/i.test(gift)) return '🛍️';
  if (/bbq|food|pizza|meal/i.test(gift)) return '🍕';
  return '🎁';
}

function App() {
  const [selectedCollege, setSelectedCollege] = useState<College | null>(null);
  const [showHome, setShowHome] = useState(true);

  const HomePage = () => (
    <Box sx={{ textAlign: 'center', py: { xs: 4, sm: 8 } }}>
      <Box sx={{ display: 'flex', justifyContent: 'center', mb: { xs: 2, sm: 4 } }}>
        <OffCampusLogo size={window.innerWidth < 600 ? 'small' : 'large'} />
      </Box>
      <Typography variant="h5" color="text.secondary" sx={{ mb: { xs: 2, sm: 4 }, fontSize: { xs: '1.1rem', sm: '1.5rem' } }}>
        The Perfect Gift Guide for College Students
      </Typography>
      <Typography variant="body1" sx={{ maxWidth: 600, mx: 'auto', mb: { xs: 3, sm: 6 }, fontSize: { xs: '0.95rem', sm: '1.1rem' } }}>
        Find the perfect gifts and experiences for students at top universities. 
        From campus essentials to memorable experiences, we've got you covered.
      </Typography>
      <Button
        variant="contained"
        size="large"
        onClick={() => setShowHome(false)}
        sx={{
          bgcolor: 'secondary.main',
          '&:hover': {
            bgcolor: 'secondary.dark',
          },
          px: { xs: 2, sm: 4 },
          py: { xs: 1, sm: 1.5 },
          fontSize: { xs: '1rem', sm: '1.1rem' },
        }}
      >
        Explore Gift Ideas
      </Button>
    </Box>
  );

  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ flexGrow: 1, minHeight: '100vh', bgcolor: 'background.default' }}>
        <AppBar position="static" elevation={0}>
          <Toolbar sx={{ px: { xs: 1, sm: 2 } }}>
            <Box sx={{ display: 'flex', alignItems: 'center', flexGrow: 1 }}>
              <OffCampusLogo size={window.innerWidth < 600 ? 'small' : 'small'} />
            </Box>
            <IconButton 
              color="inherit" 
              onClick={() => setShowHome(true)}
              sx={{ ml: { xs: 1, sm: 2 } }}
            >
              <HomeIcon sx={{ fontSize: { xs: 28, sm: 32 } }} />
            </IconButton>
          </Toolbar>
        </AppBar>

        <Container maxWidth="lg" sx={{ mt: { xs: 2, sm: 4 }, mb: { xs: 2, sm: 4 }, px: { xs: 0.5, sm: 2 } }}>
          {showHome ? (
            <HomePage />
          ) : (
            <Paper elevation={0} sx={{ p: { xs: 2, sm: 4 }, borderRadius: 4, bgcolor: 'white' }}>
              <Typography variant="h4" component="h1" gutterBottom sx={{ color: 'primary.main', fontSize: { xs: '1.4rem', sm: '2rem' }, mb: 3 }}>
                Find the Perfect Gift
              </Typography>
              <Typography variant="subtitle1" color="text.secondary" sx={{ mb: { xs: 2, sm: 4 }, fontSize: { xs: '1rem', sm: '1.2rem' } }}>
                Select a college to discover gift ideas and popular spots near campus
              </Typography>
              <Autocomplete
                options={colleges}
                getOptionLabel={(option) => option.name}
                value={selectedCollege}
                onChange={(event, newValue) => {
                  setSelectedCollege(newValue);
                }}
                renderInput={(params) => (
                  <TextField
                    {...params}
                    label="Select a College"
                    variant="outlined"
                    fullWidth
                    sx={{ mb: { xs: 2, sm: 4 } }}
                  />
                )}
              />

              {selectedCollege && (
                <Box sx={{ mt: { xs: 2, sm: 4 } }}>
                  <Typography variant="h5" gutterBottom sx={{ color: 'primary.main', fontSize: { xs: '1.2rem', sm: '1.7rem' }, mb: 3, fontWeight: 700, letterSpacing: '-0.01em' }}>
                    Popular Near {selectedCollege.name}
                  </Typography>
                  <Grid container spacing={{ xs: 3, sm: 4 }}>
                    {selectedCollege.hotspots.map((spot, index) => (
                      <Grid item xs={12} sm={6} md={4} key={index}>
                        <Card elevation={3} sx={{ height: '100%', display: 'flex', flexDirection: 'column', minHeight: { xs: 200, sm: 240 }, borderRadius: 3, boxShadow: '0 2px 16px rgba(44,62,80,0.07)' }}>
                          <CardContent sx={{ flexGrow: 1, p: { xs: 2, sm: 3 }, display: 'flex', flexDirection: 'column', gap: 1 }}>
                            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                              <Box sx={{ color: 'primary.main', mr: 1, fontSize: 28 }}>
                                {spot.icon}
                              </Box>
                              <Typography variant="h6" component="div" sx={{ fontSize: { xs: '1.15rem', sm: '1.25rem' }, fontWeight: 700, flex: 1, overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'nowrap' }}>
                                <Link href={spot.website} target="_blank" rel="noopener noreferrer" color="primary.main" underline="hover" sx={{ fontWeight: 700 }}>
                                  {spot.name}
                                </Link>
                              </Typography>
                              <Chip
                                label={spot.category}
                                size="small"
                                sx={{ bgcolor: 'primary.main', color: 'white', fontSize: { xs: '0.8rem', sm: '0.9rem' }, fontWeight: 500, ml: 1 }}
                              />
                            </Box>
                            <Typography variant="body2" color="text.secondary" sx={{ mb: 0, fontSize: { xs: '0.97rem', sm: '1.05rem' }, fontWeight: 400, lineHeight: 1.5, minHeight: 38, color: '#6c757d' }}>
                              {spot.description.length > 90 ? spot.description.slice(0, 87) + '...' : spot.description}
                            </Typography>
                            <Box sx={{ display: 'flex', alignItems: 'center', mt: 1 }}>
                              <Typography sx={{ color: 'text.secondary', fontSize: { xs: '0.95rem', sm: '1.05rem' }, fontWeight: 500 }}>
                                {spot.priceRange}
                              </Typography>
                              <Box sx={{ ml: 2, display: 'flex', gap: 1 }}>
                                {spot.giftIdeas.map((gift, idx) => (
                                  <Box key={idx} sx={{ fontSize: { xs: 22, sm: 24 } }} title={gift}>
                                    {getGiftIcon(gift)}
                                  </Box>
                                ))}
                              </Box>
                            </Box>
                          </CardContent>
                        </Card>
                      </Grid>
                    ))}
                  </Grid>
                </Box>
              )}
            </Paper>
          )}
        </Container>
      </Box>
    </ThemeProvider>
  );
}

export default App;
